import { html, raw } from 'hono/html';

import type { GradePreset } from './home';

const KEYPAD_SOUND =
  'data:audio/mp3;base64,';
const OK_SOUND =
  'data:audio/mp3;base64,';

const MODULE_SOURCE = `
(() => {
  const gradePresets = JSON.parse(
    document.getElementById('grade-presets')?.textContent ?? '[]'
  );
  const STORAGE_KEY = 'mathquest:progress:v1';
  const SOUND_STORAGE_KEY = 'mathquest:sound-enabled';
  const WORKING_STORAGE_KEY = 'mathquest:show-working';
  const FOCUS_STORAGE_KEY = 'mathquest:focus-mode';
  const DEFAULT_SESSION_TOTAL = 10;
  const SOUND_SOURCES = {
    keypad: 'data:audio/mp3;base64,',
    ok: 'data:audio/mp3;base64,',
  };

  const $ = (selector) => document.querySelector(selector);
  const $$ = (selector) => Array.from(document.querySelectorAll(selector));

  const gradeButtons = $$('[data-grade-id]');
  const questionEl = $('#question');
  const startButton = $('#start-button');
  const keypadButtons = $$('[data-keypad]');
  const answerEl = $('#answer-display');
  const feedbackEl = $('#feedback');
  const totalEl = $('#stats-total');
  const correctEl = $('#stats-correct');
  const streakEl = $('#stats-streak');
  const lastPlayedEl = $('#stats-last-played');
  const resetButton = $('#reset-progress');
  const soundToggleButton = $('#toggle-sound');
  const stepsToggleButton = $('#toggle-steps');
  const workingContainer = $('#working-container');
  const workingEmpty = $('#working-empty');
  const workingList = $('#working-steps');
  const focusToggleButton = $('#toggle-focus');
  const rootEl = document.getElementById('app-root');
  const navEl = document.getElementById('top-nav');
  const heroEl = document.getElementById('hero');
  const gradePanelEl = document.getElementById('grade-panel');
  const statsPanelEl = document.getElementById('stats-panel');
  const questionCountRadios = Array.from(
    document.querySelectorAll('input[name="question-count"]')
  );
  const focusExitButton = document.getElementById('focus-exit');
  const questionCountDefaultLabel = document.getElementById('question-count-default');
  const questionCountSaveButton = document.getElementById('question-count-save');
  const questionCountToast = document.getElementById('question-count-toast');
  const STEP_COLORS = ['#2563eb', '#16a34a', '#f97316', '#d946ef'];

  const playSound = (variant) => {
    if (!soundEnabled) return;
    const src = SOUND_SOURCES[variant] ?? SOUND_SOURCES.keypad;
    try {
      const audio = new Audio(src);
      audio.volume = variant === 'ok' ? 0.8 : 0.6;
      audio.play().catch(() => audio.remove());
      audio.addEventListener('ended', () => audio.remove(), { once: true });
    } catch (e) {
      console.warn('sound playback failed', e);
    }
  };

  const toDateString = (iso) => {
    if (!iso) return 'まだ記録はありません';
    try {
      const d = new Date(iso);
      return [
        String(d.getFullYear()) + '年',
        String(d.getMonth() + 1) + '月',
        String(d.getDate()) + '日',
      ].join('');
    } catch (e) {
      return 'まだ記録はありません';
    }
  };

  const defaultProgress = () => ({
    totalAnswered: 0,
    totalCorrect: 0,
    streak: 0,
    lastAnsweredAt: null,
    lastGrade: 'grade-1',
  });

  const loadProgress = () => {
    try {
      const raw = localStorage.getItem(STORAGE_KEY);
      if (!raw) return defaultProgress();
      const parsed = JSON.parse(raw);
      return {
        ...defaultProgress(),
        ...parsed,
      };
    } catch (e) {
      console.warn('failed to read progress from localStorage', e);
      return defaultProgress();
    }
  };

  const saveProgress = (progress) => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(progress));
  };

  const loadSoundEnabled = () => {
    try {
      const raw = localStorage.getItem(SOUND_STORAGE_KEY);
      if (raw === 'false') return false;
      if (raw === 'true') return true;
    } catch (e) {
      console.warn('failed to read sound preference', e);
    }
    return true;
  };

  const loadFocusEnabled = () => {
    try {
      const raw = localStorage.getItem(FOCUS_STORAGE_KEY);
      if (raw === 'true') return true;
      if (raw === 'false') return false;
    } catch (e) {
      console.warn('failed to read focus preference', e);
    }
    return false;
  };

  const QUESTION_COUNT_STORAGE_KEY = 'mathquest:question-count-default';

  const loadQuestionCountDefault = () => {
    try {
      const raw = localStorage.getItem(QUESTION_COUNT_STORAGE_KEY);
      const value = Number(raw);
      if (Number.isFinite(value) && value > 0) return value;
    } catch (e) {
      console.warn('failed to read question count preference', e);
    }
    return DEFAULT_SESSION_TOTAL;
  };

  let questionCountDefault = loadQuestionCountDefault();

  const updateQuestionCountDefaultLabel = () => {
    if (questionCountDefaultLabel) {
      questionCountDefaultLabel.textContent =
        '現在の初期値: ' + questionCountDefault + '問';
    }
  };

  const applyQuestionCountDefault = () => {
    let matched = false;
    questionCountRadios.forEach((radio) => {
      const value = Number(radio.value);
      if (value === questionCountDefault) {
        radio.checked = true;
        matched = true;
      }
    });
    if (!matched && questionCountRadios[0]) {
      questionCountDefault = Number(questionCountRadios[0].value);
      questionCountRadios[0].checked = true;
      try {
        localStorage.setItem(
          QUESTION_COUNT_STORAGE_KEY,
          String(questionCountDefault)
        );
      } catch (e) {
        console.warn('failed to persist question count preference', e);
      }
    }
    updateQuestionCountDefaultLabel();
  };

  applyQuestionCountDefault();

  const loadWorkingEnabled = () => {
    try {
      const raw = localStorage.getItem(WORKING_STORAGE_KEY);
      if (raw === 'false') return false;
      if (raw === 'true') return true;
    } catch (e) {
      console.warn('failed to read working preference', e);
    }
    return true;
  };

  const state = {
    progress: loadProgress(),
    answerBuffer: '',
    currentQuestion: null,
    selectedGrade: 'grade-1',
    awaitingAdvance: false,
    sessionActive: false,
    sessionTotal: questionCountDefault,
    sessionAnswered: 0,
    lastAnswer: null,
  };

  let soundEnabled = loadSoundEnabled();
  let workingEnabled = loadWorkingEnabled();
  let focusEnabled = loadFocusEnabled();
  let cachedWorkingLines = [];
  let feedbackTimer = null;
  let questionCountToastTimer = null;
  const FEEDBACK_HIDE_DELAY = 2000;

  const updateSoundToggle = () => {
    if (!soundToggleButton) return;
    soundToggleButton.dataset.state = soundEnabled ? 'on' : 'off';
    soundToggleButton.textContent = soundEnabled ? '🔊 効果音: ON' : '🔈 効果音: OFF';
  };

  const updateWorkingVisibility = () => {
    if (!workingContainer) return;
    workingContainer.dataset.visible = workingEnabled ? 'on' : 'off';
    const shouldHide = !workingEnabled || (focusEnabled && state.sessionActive);
    workingContainer.classList.toggle('hidden', shouldHide);
  };

  const updateWorkingToggle = () => {
    if (stepsToggleButton) {
      stepsToggleButton.dataset.state = workingEnabled ? 'on' : 'off';
      stepsToggleButton.textContent = workingEnabled
        ? '🧮 途中式: ON'
        : '🧮 途中式: OFF';
    }
    updateWorkingVisibility();
  };

  const updateFocusToggle = () => {
    if (!focusToggleButton) return;
    focusToggleButton.dataset.state = focusEnabled ? 'on' : 'off';
    focusToggleButton.textContent = focusEnabled
      ? '🎯 集中: ON'
      : '🎯 集中: OFF';
  };

  const applyFocusLayout = () => {
    const active = focusEnabled && state.sessionActive;
    const structuralTargets = [navEl, heroEl, gradePanelEl, statsPanelEl];
    structuralTargets.forEach((el) => {
      if (!el) return;
      if (active) el.classList.add('hidden');
      else el.classList.remove('hidden');
    });
    updateWorkingVisibility();
    if (focusExitButton) focusExitButton.classList.toggle('hidden', !active);
    if (rootEl) rootEl.dataset.focusMode = active ? 'on' : 'off';
  };

  const updateKeypadState = () => {
    keypadButtons.forEach((button) => {
      const action = button.dataset.keypad;
      if (!action) return;
      const awaiting = workingEnabled && state.awaitingAdvance;
      const noQuestion = !state.currentQuestion;
      const shouldDisable = noQuestion || (awaiting && action !== 'submit');
      if (shouldDisable) {
        button.setAttribute('disabled', 'true');
        button.classList.add('opacity-50', 'cursor-not-allowed');
      } else {
        button.removeAttribute('disabled');
        button.classList.remove('opacity-50', 'cursor-not-allowed');
      }
      if (action === 'submit') {
        button.textContent = awaiting ? '»' : '=';
        button.setAttribute('aria-label', awaiting ? 'つぎの問題へ' : 'こたえを送信');
      }
    });
  };

  const applyWorkingLines = () => {
    if (!workingList || !workingEmpty) return;
    if (!cachedWorkingLines || cachedWorkingLines.length === 0) {
      workingList.replaceChildren();
      workingEmpty.classList.remove('hidden');
      return;
    }
    const items = cachedWorkingLines.map((entry) => {
      const li = document.createElement('li');
      li.className =
        'rounded-2xl bg-white/70 px-4 py-3 text-center text-base font-semibold shadow-sm';
      li.textContent = entry.text;
      if (entry.color) {
        li.style.color = entry.color;
        li.style.borderLeft = '4px solid ' + entry.color;
        li.style.paddingLeft = '12px';
      } else {
        li.style.color = '';
        li.style.borderLeft = '';
        li.style.paddingLeft = '';
      }
      return li;
    });
    workingList.replaceChildren(...items);
    workingEmpty.classList.add('hidden');
  };

  const evaluatePrimary = (a, b, op) => {
    if (typeof a !== 'number' || typeof b !== 'number') return NaN;
    if (op === '+') return a + b;
    if (op === '-') return a - b;
    if (op === '×') return a * b;
    return NaN;
  };

  const renderQuestionExpression = (question, highlightColors, finalAnswer) => {
    if (!questionEl || !question) return;
    const extras = Array.isArray(question.extras) ? question.extras : [];
    const tokenSpecs = [
      { text: String(question.a), step: 0 },
      { text: question.op, step: 0 },
      { text: String(question.b), step: 0 },
    ];
    extras.forEach((extra, index) => {
      const stepIndex = index + 1;
      tokenSpecs.push({ text: extra.op, step: stepIndex });
      tokenSpecs.push({ text: String(extra.value), step: stepIndex });
    });
    const equalsTokenIndex = tokenSpecs.length;
    tokenSpecs.push({ text: '=', step: null });
    const displayAnswer =
      finalAnswer !== undefined && finalAnswer !== null ? String(finalAnswer) : '？';
    tokenSpecs.push({ text: displayAnswer, step: null });

    const ariaLabel = tokenSpecs
      .map((token) => token.text)
      .join(' ')
      .replace(/\s+/g, ' ')
      .trim();
    questionEl.setAttribute('aria-label', ariaLabel);
    questionEl.dataset.expression = ariaLabel;

    const elements = tokenSpecs.map((token, index) => {
      const span = document.createElement('span');
      span.className =
        'inline-flex min-w-[2.75ch] items-center justify-center px-2';
      span.textContent = token.text;
      const color =
        typeof token.step === 'number' &&
        highlightColors &&
        highlightColors.length > token.step
          ? highlightColors[token.step]
          : null;
      span.style.color = color && index !== equalsTokenIndex && index !== equalsTokenIndex + 1 ? color : '';
      return span;
    });

    questionEl.replaceChildren(...elements);
  };

  const renderWorkingSteps = (question, correctAnswer) => {
    if (!question) {
      cachedWorkingLines = [];
      state.lastAnswer = null;
      applyWorkingLines();
      if (state.currentQuestion)
        renderQuestionExpression(state.currentQuestion, undefined, state.lastAnswer);
      return;
    }
    const extras = Array.isArray(question.extras) ? question.extras : [];
    const lines = [];
    const colorForStep = (index) => STEP_COLORS[index % STEP_COLORS.length];

    const firstResult = evaluatePrimary(question.a, question.b, question.op);
    if (Number.isFinite(firstResult)) {
      lines.push({
        text:
          String(question.a) +
          ' ' +
          question.op +
          ' ' +
          String(question.b) +
          ' = ' +
          String(firstResult),
        color: colorForStep(0),
      });
    }

    let current = firstResult;
    extras.forEach((extra, index) => {
      if (!extra || (extra.op !== '+' && extra.op !== '-')) return;
      if (!Number.isFinite(current)) return;
      const value = Number(extra.value);
      if (!Number.isFinite(value)) return;
      const next = extra.op === '+' ? current + value : current - value;
      lines.push({
        text:
          String(current) + ' ' + extra.op + ' ' + String(value) + ' = ' + String(next),
        color: colorForStep(index + 1),
      });
      current = next;
    });

    if (typeof correctAnswer === 'number' && lines.length > 0) {
      const last = lines[lines.length - 1];
      last.text = last.text.replace(/=\s*[^=]+$/, '= ' + correctAnswer);
    }

    cachedWorkingLines = lines;
    applyWorkingLines();

    const highlightColors = lines.map((line) => line.color);
    const finalAnswer =
      typeof correctAnswer === 'number' ? String(correctAnswer) : undefined;
    state.lastAnswer = finalAnswer || null;
    renderQuestionExpression(question, highlightColors, finalAnswer);
  };

  const selectedQuestionTotal = () => {
    const active = questionCountRadios.find((radio) => radio && radio.checked);
    const value = active ? Number(active.value) : DEFAULT_SESSION_TOTAL;
    return Number.isFinite(value) && value > 0 ? value : DEFAULT_SESSION_TOTAL;
  };

  const finishSession = (message) => {
    state.sessionActive = false;
    state.sessionAnswered = 0;
    state.awaitingAdvance = false;
    state.currentQuestion = null;
    state.sessionTotal = selectedQuestionTotal();
    state.lastAnswer = null;
    setAnswerBuffer('');
    renderWorkingSteps(null);
    if (questionEl) {
      questionEl.textContent = message || 'おつかれさま！スタートで再開できるよ';
      questionEl.removeAttribute('data-expression');
      delete questionEl.dataset.a;
      delete questionEl.dataset.b;
      delete questionEl.dataset.op;
      delete questionEl.dataset.extras;
    }
    showFeedback('info', message || 'セッションが終了したよ');
    updateKeypadState();
    if (focusEnabled) {
      focusEnabled = false;
      try {
        localStorage.setItem(FOCUS_STORAGE_KEY, 'false');
      } catch (e) {
        console.warn('failed to persist focus preference', e);
      }
      updateFocusToggle();
    }
    applyFocusLayout();
  };

  soundToggleButton?.addEventListener('click', () => {
    soundEnabled = !soundEnabled;
    try {
      localStorage.setItem(SOUND_STORAGE_KEY, String(soundEnabled));
    } catch (e) {
      console.warn('failed to persist sound preference', e);
    }
    updateSoundToggle();
    if (soundEnabled) playSound('ok');
  });

  focusToggleButton?.addEventListener('click', () => {
    focusEnabled = !focusEnabled;
    try {
      localStorage.setItem(FOCUS_STORAGE_KEY, String(focusEnabled));
    } catch (e) {
      console.warn('failed to persist focus preference', e);
    }
    updateFocusToggle();
    applyFocusLayout();
  });

  stepsToggleButton?.addEventListener('click', () => {
    workingEnabled = !workingEnabled;
    try {
      localStorage.setItem(WORKING_STORAGE_KEY, String(workingEnabled));
    } catch (e) {
      console.warn('failed to persist working preference', e);
    }
    updateWorkingToggle();
    if (workingEnabled) {
      applyWorkingLines();
      if (cachedWorkingLines.length > 0 && state.currentQuestion) {
        const colors = cachedWorkingLines.map((entry, index) => {
          return entry && entry.color
            ? entry.color
            : STEP_COLORS[index % STEP_COLORS.length];
        });
        renderQuestionExpression(state.currentQuestion, colors, state.lastAnswer);
      }
    } else {
      state.awaitingAdvance = false;
      applyWorkingLines();
      if (state.currentQuestion)
        renderQuestionExpression(state.currentQuestion, undefined, state.lastAnswer);
    }
    updateKeypadState();
    if (!workingEnabled) hideFeedback();
  });

  questionCountRadios.forEach((radio) => {
    radio.addEventListener('change', () => {
      if (!radio.checked) return;
      if (!state.sessionActive) state.sessionTotal = selectedQuestionTotal();
    });
  });

  questionCountSaveButton?.addEventListener('click', () => {
    questionCountDefault = selectedQuestionTotal();
    try {
      localStorage.setItem(
        QUESTION_COUNT_STORAGE_KEY,
        String(questionCountDefault)
      );
    } catch (e) {
      console.warn('failed to persist question count preference', e);
    }
    if (!state.sessionActive) state.sessionTotal = questionCountDefault;
    applyQuestionCountDefault();
    if (questionCountToast) {
      questionCountToast.textContent =
        '初期値を ' + questionCountDefault + '問に更新しました';
      questionCountToast.classList.remove('hidden');
      if (questionCountToastTimer) clearTimeout(questionCountToastTimer);
      questionCountToastTimer = window.setTimeout(() => {
        questionCountToast.classList.add('hidden');
        questionCountToastTimer = null;
      }, 2000);
    }
  });

  focusExitButton?.addEventListener('click', () => {
    if (!focusEnabled) return;
    focusEnabled = false;
    try {
      localStorage.setItem(FOCUS_STORAGE_KEY, 'false');
    } catch (e) {
      console.warn('failed to persist focus preference', e);
    }
    updateFocusToggle();
    applyFocusLayout();
  });

  const applyActiveGradeStyles = () => {
    gradeButtons.forEach((button) => {
      const isActive = button.dataset.gradeId === state.selectedGrade;
      button.classList.toggle('border-[var(--mq-primary)]', isActive);
      button.classList.toggle('bg-[var(--mq-surface-strong)]', isActive);
      button.classList.toggle('shadow-xl', isActive);
      button.classList.toggle('ring-2', isActive);
      button.classList.toggle('ring-[var(--mq-accent)]', isActive);
      button.classList.toggle('text-[var(--mq-ink)]', isActive);
      button.setAttribute('aria-pressed', isActive ? 'true' : 'false');
    });
  };

  const renderProgress = () => {
    totalEl.textContent = state.progress.totalAnswered + '問';
    correctEl.textContent = state.progress.totalCorrect + '問';
    streakEl.textContent = state.progress.streak + '問';
    lastPlayedEl.textContent = toDateString(state.progress.lastAnsweredAt);
  };

  const hideFeedback = () => {
    if (feedbackTimer) {
      clearTimeout(feedbackTimer);
      feedbackTimer = null;
    }
    if (feedbackEl) feedbackEl.classList.add('opacity-0');
  };

  const showFeedback = (type, message) => {
    if (!feedbackEl) return;
    if (feedbackTimer) {
      clearTimeout(feedbackTimer);
      feedbackTimer = null;
    }
    feedbackEl.textContent = message;
    feedbackEl.dataset.variant = type;
    feedbackEl.classList.remove('opacity-0');
    if (!workingEnabled) {
      feedbackTimer = window.setTimeout(() => {
        feedbackEl.classList.add('opacity-0');
        feedbackTimer = null;
      }, FEEDBACK_HIDE_DELAY);
    }
  };

  const setAnswerBuffer = (value) => {
    state.answerBuffer = value.replace(/^0+(d)/, '$1').slice(0, 6);
    answerEl.textContent = state.answerBuffer || '？';
  };

  const attachKeypad = () => {
    keypadButtons.forEach((button) => {
      const action = button.dataset.keypad;
      if (action === 'digit') {
        button.addEventListener('click', () => {
          setAnswerBuffer(state.answerBuffer + button.dataset.value);
          playSound('keypad');
        });
      }
      if (action === 'delete') {
        button.addEventListener('click', () => {
          setAnswerBuffer(state.answerBuffer.slice(0, -1));
          playSound('keypad');
        });
      }
      if (action === 'submit') {
        button.addEventListener('click', () => {
          playSound('ok');
          submitAnswer();
        });
      }
    });
  };

  const currentPreset = () =>
    gradePresets.find((preset) => preset.id === state.selectedGrade) ??
    gradePresets[0];

  const updateGradeDescription = () => {
    const preset = currentPreset();
    document.getElementById('grade-name').textContent = preset.label + ' のもんだい';
    document.getElementById('grade-description').textContent = preset.description;
  };

  const nextQuestion = async () => {
    state.awaitingAdvance = false;
    renderWorkingSteps(null);
    updateKeypadState();
    const preset = currentPreset();
    const response = await fetch('/apis/quiz/questions/next', {
      method: 'POST',
      headers: { 'content-type': 'application/json' },
      body: JSON.stringify({
        mode: preset.mode,
        max: preset.max,
        gradeId: state.selectedGrade,
      }),
    });
    if (!response.ok) {
      showFeedback('error', '問題の取得に失敗しました');
      return;
    }
    const { question } = await response.json();
    state.currentQuestion = question;
    state.lastAnswer = null;
    questionEl.dataset.a = String(question.a);
    questionEl.dataset.b = String(question.b);
    questionEl.dataset.op = question.op;
    if (Array.isArray(question.extras) && question.extras.length > 0) {
      questionEl.dataset.extras = JSON.stringify(question.extras);
    } else {
      delete questionEl.dataset.extras;
    }
    renderQuestionExpression(question);
    setAnswerBuffer('');
    updateKeypadState();
  };

  const submitAnswer = async () => {
    if (workingEnabled && state.awaitingAdvance) {
      state.awaitingAdvance = false;
      updateKeypadState();
      hideFeedback();
      await nextQuestion();
      return;
    }
    const currentQuestion = state.currentQuestion;
    if (!currentQuestion) {
      showFeedback('info', 'スタートボタンで問題をはじめましょう');
      return;
    }
    if (!state.answerBuffer) {
      showFeedback('info', 'こたえを入力してね');
      return;
    }
    const value = Number(state.answerBuffer);
    if (Number.isNaN(value)) {
      showFeedback('error', '数字だけを入力してね');
      return;
    }
    const preset = currentPreset();
    const response = await fetch('/apis/quiz/answers/check', {
      method: 'POST',
      headers: { 'content-type': 'application/json' },
      body: JSON.stringify({
        question: {
          a: currentQuestion.a,
          b: currentQuestion.b,
          op: currentQuestion.op,
          extras: Array.isArray(currentQuestion.extras)
            ? currentQuestion.extras
            : [],
        },
        value,
        gradeId: state.selectedGrade,
        mode: preset.mode,
        max: preset.max,
      }),
    });
    if (!response.ok) {
      showFeedback('error', '採点に失敗しました');
      return;
    }
    const { ok, correctAnswer } = await response.json();
    state.progress.totalAnswered += 1;
    state.progress.lastAnsweredAt = new Date().toISOString();
    state.progress.lastGrade = state.selectedGrade;
    if (state.sessionActive) {
      state.sessionAnswered += 1;
    }
    if (ok) {
      state.progress.totalCorrect += 1;
      state.progress.streak += 1;
      showFeedback('success', 'せいかい！');
    } else {
      state.progress.streak = 0;
      showFeedback('error', 'ざんねん… せいかいは ' + correctAnswer);
    }
    saveProgress(state.progress);
    renderProgress();
    renderWorkingSteps(currentQuestion, correctAnswer);
    if (state.sessionActive && state.sessionAnswered >= state.sessionTotal) {
      finishSession('セッションおつかれさま！またスタートしてね');
      return;
    }
    if (workingEnabled) {
      state.awaitingAdvance = true;
      updateKeypadState();
      return;
    }
    await nextQuestion();
  };

  gradeButtons.forEach((button) => {
    button.addEventListener('click', () => {
      state.selectedGrade = button.dataset.gradeId;
      applyActiveGradeStyles();
      updateGradeDescription();
      playSound('keypad');
      if (state.progress.lastGrade !== state.selectedGrade) {
        state.progress.streak = 0;
        saveProgress(state.progress);
        renderProgress();
      }
    });
  });

  startButton.addEventListener('click', () => {
    hideFeedback();
    state.selectedGrade = state.progress.lastGrade
      ? String(state.progress.lastGrade)
      : state.selectedGrade;
    applyActiveGradeStyles();
    updateGradeDescription();
    state.sessionTotal = selectedQuestionTotal();
    state.sessionAnswered = 0;
    state.sessionActive = true;
    state.awaitingAdvance = false;
    applyFocusLayout();
    updateKeypadState();
    nextQuestion();
    playSound('keypad');
  });

  document.addEventListener('keydown', (event) => {
    if (event.key === 'Escape') {
      if (focusEnabled) {
        focusEnabled = false;
        try {
          localStorage.setItem(FOCUS_STORAGE_KEY, 'false');
        } catch (e) {
          console.warn('failed to persist focus preference', e);
        }
        updateFocusToggle();
        applyFocusLayout();
      }
      return;
    }
    if (workingEnabled && state.awaitingAdvance) {
      if (event.key === 'Enter') {
        playSound('ok');
        submitAnswer();
      }
      return;
    }
    if (event.key >= '0' && event.key <= '9') {
      setAnswerBuffer(state.answerBuffer + event.key);
      playSound('keypad');
    }
    if (event.key === 'Backspace') {
      setAnswerBuffer(state.answerBuffer.slice(0, -1));
      playSound('keypad');
    }
    if (event.key === 'Enter') {
      playSound('ok');
      submitAnswer();
    }
  });

  resetButton.addEventListener('click', () => {
    if (!confirm('学習記録をリセットしますか？')) return;
    state.progress = defaultProgress();
    saveProgress(state.progress);
    renderProgress();
    setAnswerBuffer('');
    feedbackEl.textContent = '';
    hideFeedback();
    playSound('ok');
  });

  state.selectedGrade = state.progress.lastGrade || 'grade-1';
  applyActiveGradeStyles();
  updateGradeDescription();
  state.sessionTotal = selectedQuestionTotal();
  renderProgress();
  attachKeypad();
  applyWorkingLines();
  updateWorkingToggle();
  updateFocusToggle();
  applyFocusLayout();
  updateKeypadState();
  updateSoundToggle();
  nextQuestion();
})();
`;

export const renderHomeClientScript = (presets: readonly GradePreset[]) => html`
  <script id="grade-presets" type="application/json">
    ${raw(JSON.stringify(presets))}
  </script>
  <script type="module">
    ${raw(MODULE_SOURCE)};
  </script>
`;
